(function($, window, document) {
  $(function(){
    
    // nav
    $("#menu-toggle").click(function(e) {
      e.preventDefault();
      $("#wrapper").toggleClass("toggled");
      var state = $('#wrapper').hasClass('toggled') ;
      $.post( "/admin/remember", { toggled: state } );
    });
    
    // shop
    $("#select-shops").change(function() {
      var id = $(this).val() ;
      $.post( "/admin/remember", { shop: id }, function() {
        window.location.replace("/admin"); 
      });
    }); 
    
    // lang
    $("#select-language").change(function() {
      var id = $(this).val() ;
      $.post( "/admin/remember", { language: id }, function() {
        location.reload() ; 
      });
    });
    
    // are you sure?, when danger is present
    $(document).on('click', '.btn-danger, a.text-danger', function(e){
      e.preventDefault() ;
      if (window.confirm("Are you sure?")) {
        $(this).closest('form').submit() ; 
      }
    }) ;
    
    // wysiwyg
    $('.wysiwyg').summernote({
      height: 300,
      toolbar: [
        ['style', ['style']],
        ['font', ['bold', 'italic', 'underline', 'clear']],
        ['para', ['ul', 'ol', 'paragraph']],
        ['table', ['table']],
        ['insert', ['link', 'hr']],
        ['view', ['fullscreen', 'codeview']]
      ],
      disableDragAndDrop: true
    });
  
  });
}(window.jQuery, window, document)); 
(function($, window, document) {
  $(function(){
    
    // dropzone
    if( $('#kudos-dropzone').length ){
      Dropzone.autoDiscover = false;
      var kudosDropzone = new Dropzone('#kudos-dropzone', {
        maxFilesize: 2, // MB
        acceptedFiles: '.png,.jpg'
      }) ; ;
      kudosDropzone.on('success', function(file) {
        kudosDropzone.removeFile(file);
      });
      kudosDropzone.on('queuecomplete', function() {
        var id = $('#thumbnails').data('id') ;
        var type = $('#thumbnails').data('type') ;
        var model = $('#thumbnails').data('model') ;
        $.get( '/admin/media/thumbnails/'+id+'/'+model+'/'+type, function( data ) {
          $('#thumbnails').html( data );
        });
      });
    }
    
    // delete image
    $(document).on('click', '#thumbnails input[type=submit]', function(e){
      e.preventDefault() ;
      if (window.confirm("Are you sure?")) {
        var close = $(this) ;
        var file = close.prev() ;
        var token = file.prev().val() ;
        $.post('/admin/media/delete', { 'file': file.val(), '_token': token })
        .done(function() {
          close.closest('div').hide() ;
        }) ;
      }
    }) ;
    
    // set default image
    $(document).on('click', '#thumbnails input[type=image]', function(e){
      e.preventDefault() ;
      var thumb = $(this) ;
      var url = $(this).closest('form').attr('action') ;
      var token = thumb.prev().prev().val() ;
      $.post(url, { '_token': token, 'file': thumb.data('src') })
      .done(function() {
        $('#thumbnails input[type=image]').removeClass('default') ;
        thumb.addClass('default') ;
      }) ;
    }) ;
     
    // lightbox
    $( '.swipebox' ).swipebox();
  
  });
}(window.jQuery, window, document)); 
(function($, window, document) {
    var unsaved = false ;

    // mark the page as having unsaved content
   $('body').on('keyup', 'form[method=POST] input, form[method=POST] textarea, .note-editable', function() {
      unsaved = true ;
    }) ;
    
    // the user submitted the form (to save) so no need to ask them.
    $(document).on('submit', 'form[method=POST]', function(){
      unsaved = false ;
      return ; 
    }) ;
    
    // Confirm with user if they try to go elsewhere
    $(window).bind('beforeunload', function(e){;      
      if(unsaved){
        return "Page unsaved";
      }
    }) ;
  
}(window.jQuery, window, document)); 
